import { useState, useEffect, useRef } from 'react';

import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import List from '@mui/material/List';
import Container from '@mui/material/Container';
import Stack from '@mui/material/Stack';

import Message from '../Message';
import style from './Chat.module.scss';

import { Typography as TypographyComponent } from '@mui/material';
import { FiSend } from 'react-icons/fi';
import CircularProgress from '@mui/material/CircularProgress';


const Chat = ({ messages, submitMessage, myId, isGeneratingAnswer, isLoadingHistory, fetchChatHistory }) => {
    const [input, setInput] = useState('');
	const [isMobileDevice, setIsMobileDevice] = useState(false);
	const messagesEndRef = useRef(null);
	const inputRef = useRef(null);


    const onChange = (e) => {
        setInput(e.currentTarget.value);
    }

	const handleKeyPress = (e) => {
        if (e.key === "Enter") {
            e.preventDefault();
            if (isGeneratingAnswer) {
                return;
            }
            onClick();
			if (isMobileDevice) {
				inputRef.current.blur();
			}
        }
    };

	const handleButtonClick_fill = (text) => {
		setInput(text);
	}

	const handleButtonClick_send = (text) => {
		submitMessage(text);
	}

	const handleButtonClick_fetch = () => {
		fetchChatHistory();
	}

	const onClick = () => {
		if (isGeneratingAnswer) {
			return;
	  	}
	  	submitMessage(input);
		setInput('');
	}

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "auto" });
    };

	useEffect(() => {
        const isMobile = /iPhone|iPad|iPod|Android/i.test(navigator.userAgent);
        setIsMobileDevice(isMobile);
    }, []); // messagesÎ•º ÏùòÏ°¥ÏÑ± Î∞∞Ïó¥ÏóêÏÑú Ï†úÍ±∞

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

	//useEffect(() => {
	//	alert(`isLoadingHistory: ${isLoadingHistory}`);
	//}, [isLoadingHistory]);


    return (
        <Container maxWidth="md" className={style.Chat__wrapper} sx={{ display: 'flex', flexDirection: 'column' }}>	

			{messages.length === 0 && (
				<div style={{ overflowY: 'scroll', height: `calc(90vh - 3.5rem)` }}>
					<Stack direction="column" sx={{ mb: '10vh', flex: '0 0 auto', flexWrap: 'wrap' }}>

						<Stack direction="row" sx={{ mb: '2vh', flex: '0 0 auto' }}>
							<Container
								sx={{ display: "flex", alignItems: "center", justifyContent: "center", height: "auto", flex: 1, marginTop: '10vh',
									fontFamily: 'NanumSquare', bgcolor: 'white', color: 'black', p: 2, borderRadius: 4, boxShadow: 'none', '&:hover': {backgroundColor: 'white'} }} disableRipple>
								<TypographyComponent variant="h5" align="center" fontWeight="bold">üôã ÏïàÎÖïÌïòÏÑ∏Ïöî Ïñ¥Îñ§ ÏÉÅÌíàÏùÑ Ï∞æÏúºÏãúÎÇòÏöî?</TypographyComponent>
							</Container>
						</Stack>
						<Stack direction="row" sx={{ mb: '2vh', flex: '0 0 auto' }}>
							<Button
								sx={{ display: "flex", justifyContent: "flex-start", height: "auto", flex: 1, top: '5vh',
									fontFamily: 'NanumSquare', bgcolor: '#F5F5F5', color: 'black', p: 2, borderRadius: 4, boxShadow: 'none', '&:hover': {backgroundColor: '#F5F5F5'} }} disableRipple>
								<TypographyComponent variant="body1" align="left" fontWeight="medium">‚ö° ÏùºÏÉÅÏ†ÅÏù∏ ÎåÄÌôîÎèÑ Í∞ÄÎä•ÌïòÏßÄÎßå ÏÉÅÌíà Ï∂îÏ≤úÏóê ÏµúÏ†ÅÌôî ÎêòÏñ¥ÏûàÏñ¥Ïöî.</TypographyComponent>
							</Button>
							<Button
								sx={{ display: "flex", justifyContent: "flex-start", height: "auto", flex: 1, top: '5vh', marginLeft: '2vh', marginRight: '2vh',
									fontFamily: 'NanumSquare', bgcolor: '#F5F5F5', color: 'black', p: 2, borderRadius: 4, boxShadow: 'none', '&:hover': {backgroundColor: '#F5F5F5'} }} disableRipple>
								<TypographyComponent variant="body1" align="left" fontWeight="medium">üöß Î∂ÄÏ†ïÌôïÌïú Ï†ïÎ≥¥Í∞Ä Ìè¨Ìï®Îê† Ïàò ÏûàÏñ¥Ïöî. Íµ¨Îß§ Ï†Ñ ÏÉÅÏÑ∏ Ï†ïÎ≥¥Î•º Íº≠ ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.</TypographyComponent>
							</Button>
							<Button
								sx={{ display: "flex", justifyContent: "flex-start", height: "auto", flex: 1, top: '5vh',
									fontFamily: 'NanumSquare', bgcolor: '#F5F5F5', color: 'black', p: 2, borderRadius: 4, boxShadow: 'none', '&:hover': {backgroundColor: '#F5F5F5'} }} disableRipple>
