import { useState, useEffect, useRef } from 'react';

import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import List from '@mui/material/List';
import Container from '@mui/material/Container';
import Stack from '@mui/material/Stack';

import Message from '../Message';
import style from './Chat.module.scss';

import { Typography as TypographyComponent } from '@mui/material';
import { FiSend } from 'react-icons/fi';
import CircularProgress from '@mui/material/CircularProgress';
import { TransitionGroup, CSSTransition } from 'react-transition-group';


const Chat = ({ messages, submitMessage, myId, isGeneratingAnswer }) => {
    const [input, setInput] = useState('');
    const messagesEndRef = useRef(null);

    const onChange = (e) => {
        setInput(e.currentTarget.value);
    }

	const handleKeyPress = (e) => {
        if (e.key === "Enter") {
            e.preventDefault();
            if (isGeneratingAnswer) {
                return;
            }
            onClick();
        }
    };

	const handleButtonClick = (text) => {
		setInput(text);
	}

	const onClick = () => {
		if (isGeneratingAnswer) {
			return;
	  	}
	  	submitMessage(input);
		setInput('');
	}

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "auto" });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    return (
        <Container maxWidth="md" className={style.Chat__wrapper}>	
			{messages.length === 0 && (
				<>
				<Container maxWidth="md" 
						sx={{ position: 'fixed', top: '10vh', left: '0', right: '0', 
						bgcolor: 'transparent', color: 'black', p: 2, borderRadius: 4, height: "100px", width: "100%" }}>
							<TypographyComponent variant="h4" align="center" fontWeight="bold">삼다수 찾아줘</TypographyComponent>
				</Container>
				<Stack direction="row" sx={{ mb: 1 }}>
					<Button
						onClick={() =>
							handleButtonClick("코브가 유연성이 얼마나 안좋은지 상세하게 알려줘")
						}
						sx={{ height: "100px", width: "33%", position: 'relative', marginLeft: 0,
							fontFamily: 'NanumSquare', bgcolor: '#F5F5F5', color: 'black', p: 2, borderRadius: 4, boxShadow: 'none', '&:hover': {backgroundColor: '#D8D8D8'} }} disableRipple>
						<TypographyComponent variant="body1" align="left" fontWeight="medium">코브가 유연성이 얼마나 안좋은지 상세하게 알려줘</TypographyComponent>
                    </Button>
                    <Button
						onClick={() =>
							handleButtonClick("제노는 지금 무슨 코딩을 하고있어? 왜 진척사항이 없어?")
						}
						sx={{ height: "100px", width: "33%", position: 'relative', top: '300px', marginLeft: '20px', marginRight: '20px',
							fontFamily: 'NanumSquare', bgcolor: '#F5F5F5', color: 'black', p: 2, borderRadius: 4, boxShadow: 'none', '&:hover': {backgroundColor: '#D8D8D8'} }} disableRipple>
                        <TypographyComponent variant="body1" align="left" fontWeight="medium">제노는 지금 무슨 코딩을 하고있어? 왜 진척사항이 없어?</TypographyComponent>
                    </Button>
                    <Button
						onClick={() =>
							handleButtonClick("사유는 왜 채식을 하는지 알려줘. 잘 이해가 안돼.")
						}
						sx={{ height: "100px", width: "33%", position: 'relative', top: '200px', marginRight: 0,
							fontFamily: 'NanumSquare', bgcolor: '#F5F5F5', color: 'black', p: 2, borderRadius: 4, boxShadow: 'none', '&:hover': {backgroundColor: '#D8D8D8'} }} disableRipple>
                        <TypographyComponent variant="body1" align="left" fontWeight="medium">사유는 왜 채식을 하는지 알려줘. 잘 이해가 안돼.</TypographyComponent>
                    </Button>
                </Stack>
				</>
			)}

			<List maxWidth="md" className={style.Chat__list} >
				{messages.map(({userId, message}, index) => 
							  <Message 
								  key={`${userId}_${index}`}
								  userId={userId}
								  myId={myId}
								  text={message}
								  showName={!index || messages[index - 1].userId !== userId}
							  />
							 )}
			<div ref={messagesEndRef} />
			</List>
			<Stack maxWidth="md" direction="row" className={style.Chat__input}>
				<TextField 
					className={style.Chat__input__textfield}
					onChange={onChange} 
					value={input} 
					//placeholder="메시지를 입력하세요."
					onKeyPress={handleKeyPress}
					multiline
					minRows={1}
					maxRows={5}

					InputProps={{
						endAdornment: (
							isGeneratingAnswer ?
          					<CircularProgress size={24} /> :
							<Button
								onClick={onClick}
								variant="contained"
								color="primary"
								className={style.Chat__input__button}
								sx={{ minWidth: '2rem', minHeight: '2rem', position: 'absolute', right: '5px', bottom: '12px' }}
							>	<FiSend color="grey"/>
						</Button>
					  )
					}}
				/>
				<Button
					variant="contained"
					color="secondary"
					className={style.Chat__button_restart}
					sx={{ minWidth: '2rem', minHeight: '2rem', position: 'absolute', left: '0%', top: '-50px' }}
					//sx={{ minWidth: '2rem', minHeight: '2rem', position: 'absolute', right: '50%', transform: 'translateX(50%)',top: '-50px' }}
				>
					Button
				</Button>
			</Stack>
        </Container>
    )
}

export default Chat;
