import React, { useState } from 'react';
import { toast } from "react-toastify";
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Stack from '@mui/material/Stack';
import { createTheme, ThemeProvider } from '@mui/material/styles';

import style from './ChatRoomEntry.module.scss';

const ChatRoomEntry = ({enterChatRoom}) => {
    const [chatRoomId, setChatRoomId] = useState('');
    const [nickname, setNickname] = useState('');

	const isInvalidNickname = () => {
	    const pattern = /^[A-Za-z0-9„Ñ±-„Öé„Öè-„Ö£Í∞Ä-Ìû£]*$/;
	    const invalid = nickname.length > 16 || nickname.length < 2 || !pattern.test(nickname);
	    return invalid;
	}

	const isInvalidChatRoomId = () => {
	    const pattern = /^[A-Za-z0-9„Ñ±-„Öé„Öè-„Ö£Í∞Ä-Ìû£][^\s]{0,14}[A-Za-z0-9„Ñ±-„Öé„Öè-„Ö£Í∞Ä-Ìû£]$/;
	    const invalid = chatRoomId.length > 16 || chatRoomId.length < 2 || !pattern.test(chatRoomId);
	    return invalid;
	}

    const onChangeChatRoomId = (e) => {
        const input = e.currentTarget.value;
        setChatRoomId(input);
    }

    const onChangeNickname = (e) => {
        const input = e.currentTarget.value;
        setNickname(input);
    }

    const onClick = () => {
        if (isInvalidChatRoomId()) {
            toast.error("ÏÇ¨Ïö©Ïûê Ïù¥Î¶ÑÏùÄ ÏïåÌååÎ≤≥ ÎòêÎäî Ïà´ÏûêÎ°ú Ïù¥Î£®Ïñ¥ÏßÑ 2~16Í∏ÄÏûêÎßå Í∞ÄÎä•Ìï©ÎãàÎã§.");
            return;
        }  

		if (isInvalidNickname()) {
            toast.error("ÎπÑÎ∞ÄÎ≤àÌò∏Îäî ÏïåÌååÎ≤≥ ÎòêÎäî Ïà´ÏûêÎ°ú Ïù¥Î£®Ïñ¥ÏßÑ 2~16Í∏ÄÏûê Î¨∏ÏûêÏûÖÎãàÎã§.");
            return;
        }

        enterChatRoom({nickname, chatRoomId});
    }
  	const handleSubmit = (e) => {
		e.preventDefault(); // Prevent page refresh on form submission
		onClick();
  	};

	const theme = createTheme({
	    components: {
		    MuiOutlinedInput: {
		        styleOverrides: {
					root: {
						'&:hover .MuiOutlinedInput-notchedOutline': {
							borderColor: '#154CCB', // replace 'your-desired-color' with the color you want
						},
						'&.Mui-focused .MuiOutlinedInput-notchedOutline': {
							borderColor: '#154CCB', // replace 'your-desired-color' with the color you want
					    },
					},
		  		},
			},
	  	},
	});

    return (
		<ThemeProvider theme={theme}>
			<form onSubmit={handleSubmit}>
				<Stack className={style.ChatRoomEntry} alignItems="center" spacing={2}>
					<div className={style.ChatRoomEntry__title}> ÎÇòÏùò Ï≤´ ÏáºÌïë ÎπÑÏÑú ÏïÑÏî®Ïò§ </div>
					<div className={style.ChatRoomEntry__subtitle}> ÌöåÏõêÍ∞ÄÏûÖ ÏóÜÏù¥ 5Ï¥àÎßåÏóê <br/> Ïù∏Í≥µÏßÄÎä• ÏáºÌïë Î©îÏù¥Ìä∏ÏôÄ ÎåÄÌôîÌïòÍ∏∞ </div>
					<TextField label="ÏÇ¨Ïö©Ïûê Ïù¥Î¶Ñ(Username)" variant="outlined" value={chatRoomId} onChange={onChangeChatRoomId}/>
					<TextField label="ÎπÑÎ∞ÄÎ≤àÌò∏(Password)" variant="outlined" value={nickname} onChange={onChangeNickname}/>
					<Button size="large" className={style.startButton} type="submit" disableRipple>ÏãúÏûëÌïòÍ∏∞</Button>
					<div className={style.ChatRoomEntry__caption}> ÏÇ¨Ïö©Ïûê Ïù¥Î¶ÑÍ≥º ÎπÑÎ∞ÄÎ≤àÌò∏Î•º Í∏∞ÏñµÌï¥Ï£ºÏÑ∏Ïöî üôÇ </div>
				</Stack>
			</form>
		</ThemeProvider>
    );

}

export default ChatRoomEntry;
