import { useState, useEffect, useRef } from 'react';

import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import List from '@mui/material/List';
import Container from '@mui/material/Container';
import Stack from '@mui/material/Stack';

import Message from '../Message';
import style from './Chat.module.scss';

import { Typography as TypographyComponent } from '@mui/material';
import { FiSend } from 'react-icons/fi';
import CircularProgress from '@mui/material/CircularProgress';

const Chat = ({ messages, submitMessage, myId, isGeneratingAnswer }) => {
    const [input, setInput] = useState('');
    const messagesEndRef = useRef(null);

    const onChange = (e) => {
        setInput(e.currentTarget.value);
    }

	const handleKeyPress = (e) => {
        if (e.key === "Enter") {
            e.preventDefault();
            if (isGeneratingAnswer) {
                return;
            }
            onClick();
        }
    };

	const handleButtonClick = (text) => {
		setInput(text);
	}

	const onClick = () => {
		if (isGeneratingAnswer) {
			return;
	  	}
	  	submitMessage(input);
		setInput('');
	}

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "auto" });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    return (
        <Container maxWidth="md" className={style.Chat__wrapper} sx={{ display: 'flex', flexDirection: 'column' }}>	

			{messages.length === 0 && (
				<>

				<Stack direction="column" sx={{ mb: '10vh', flex: '0 0 auto', flexWrap: 'wrap' }}>

					<Stack direction="row" sx={{ mb: '2vh', flex: '0 0 auto' }}>
						<Button
							sx={{ display: "flex", alignItems: "center", justifyContent: "center", height: "auto", flex: 1, top: '30vh',
								fontFamily: 'NanumSquare', bgcolor: '#F5F5F5', color: 'black', p: 2, borderRadius: 4, boxShadow: 'none', '&:hover': {backgroundColor: '#D8D8D8'} }} disableRipple>
							<TypographyComponent variant="h4" align="center" fontWeight="bold">ÏÇºÎã§Ïàò Ï∞æÏïÑÏ§ò</TypographyComponent>
						</Button>
					</Stack>
					<Stack direction="row" sx={{ mb: '2vh', flex: '0 0 auto' }}>
						<Button
							onClick={() =>
								handleButtonClick("Î¨¥ÎùºÎ≤® ÏÉùÏàò 2L 20Í∞ú ÏµúÏ†ÄÍ∞Ä Ï∞æÏïÑÏ§ò")
							}
							sx={{ display: "flex", alignItems: "center", justifyContent: "center", height: "auto", flex: 1, top: '30vh',
								fontFamily: 'NanumSquare', bgcolor: '#F5F5F5', color: 'black', p: 2, borderRadius: 4, boxShadow: 'none', '&:hover': {backgroundColor: '#D8D8D8'} }} disableRipple>
							<TypographyComponent variant="body1" align="left" fontWeight="medium">üí° "Î¨¥ÎùºÎ≤® ÏÉùÏàò 2L 20Í∞ú ÏµúÏ†ÄÍ∞Ä Ï∞æÏïÑÏ§ò" : Îã§ÏñëÌïú Ï°∞Í±¥ÏùÑ Ìè¨Ìï®ÌïòÏó¨ Íµ¨Ï≤¥Ï†ÅÏúºÎ°ú Ï∞æÏùÑ Ïàò ÏûàÏñ¥Ïöî.</TypographyComponent>
						</Button>
						<Button
							onClick={() =>
								handleButtonClick("Ï†úÎÖ∏Îäî ÏßÄÍ∏à Î¨¥Ïä® ÏΩîÎî©ÏùÑ ÌïòÍ≥†ÏûàÏñ¥? Ïôú ÏßÑÏ≤ôÏÇ¨Ìï≠Ïù¥ ÏóÜÏñ¥?")
							}
							sx={{ display: "flex", alignItems: "center", justifyContent: "center", height: "auto", flex: 1, top: '30vh', marginLeft: '2vh', marginRight: '2vh',
								fontFamily: 'NanumSquare', bgcolor: '#F5F5F5', color: 'black', p: 2, borderRadius: 4, boxShadow: 'none', '&:hover': {backgroundColor: '#D8D8D8'} }} disableRipple>
							<TypographyComponent variant="body1" align="left" fontWeight="medium">üí° Ï†úÎÖ∏Îäî ÏßÄÍ∏à Î¨¥Ïä® ÏΩîÎî©ÏùÑ ÌïòÍ≥†ÏûàÏñ¥? Ïôú ÏßÑÏ≤ôÏÇ¨Ìï≠Ïù¥ ÏóÜÏñ¥?</TypographyComponent>
						</Button>
						<Button
							onClick={() =>
								handleButtonClick("ÏÇ¨Ïú†Îäî Ïôú Ï±ÑÏãùÏùÑ ÌïòÎäîÏßÄ ÏïåÎ†§Ï§ò. Ïûò Ïù¥Ìï¥Í∞Ä ÏïàÎèº.")
							}
							sx={{ display: "flex", alignItems: "center", justifyContent: "center", height: "auto", flex: 1, top: '30vh',
								fontFamily: 'NanumSquare', bgcolor: '#F5F5F5', color: 'black', p: 2, borderRadius: 4, boxShadow: 'none', '&:hover': {backgroundColor: '#D8D8D8'} }} disableRipple>
							<TypographyComponent variant="body1" align="left" fontWeight="medium">üí° ÏÇ¨Ïú†Îäî Ïôú Ï±ÑÏãùÏùÑ ÌïòÎäîÏßÄ ÏïåÎ†§Ï§ò. Ïûò Ïù¥Ìï¥Í∞Ä ÏïàÎèº.</TypographyComponent>
						</Button>
					</Stack>
					<Stack direction="row" sx={{ mb: '2vh', flex: '0 0 auto' }}>
						<Button
							onClick={() =>
								handleButtonClick("ÏΩîÎ∏åÍ∞Ä Ïú†Ïó∞ÏÑ±Ïù¥ ÏñºÎßàÎÇò ÏïàÏ¢ãÏùÄÏßÄ ÏÉÅÏÑ∏ÌïòÍ≤å ÏïåÎ†§Ï§ò")
							}
							sx={{ display: "flex", alignItems: "center", justifyContent: "center", height: "auto", flex: 1, top: '30vh',
								fontFamily: 'NanumSquare', bgcolor: '#F5F5F5', color: 'black', p: 2, borderRadius: 4, boxShadow: 'none', '&:hover': {backgroundColor: '#D8D8D8'} }} disableRipple>
							<TypographyComponent variant="body1" align="left" fontWeight="medium">ÏΩîÎ∏åÍ∞Ä Ïú†Ïó∞ÏÑ±Ïù¥ ÏñºÎßàÎÇò ÏïàÏ¢ãÏùÄÏßÄ ÏÉÅÏÑ∏ÌïòÍ≤å ÏïåÎ†§Ï§ò11</TypographyComponent>
						</Button>
						<Button
							onClick={() =>
								handleButtonClick("Ï†úÎÖ∏Îäî ÏßÄÍ∏à Î¨¥Ïä® ÏΩîÎî©ÏùÑ ÌïòÍ≥†ÏûàÏñ¥? Ïôú ÏßÑÏ≤ôÏÇ¨Ìï≠Ïù¥ ÏóÜÏñ¥?")
