import { useState, useEffect, useRef } from 'react';

import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import List from '@mui/material/List';
import Container from '@mui/material/Container';
import Stack from '@mui/material/Stack';

import Message from '../Message';
import style from './Chat.module.scss';

import { Typography as TypographyComponent } from '@mui/material';
import { FiSend } from 'react-icons/fi';
import CircularProgress from '@mui/material/CircularProgress';

const Chat = ({ messages, submitMessage, myId, isGeneratingAnswer }) => {
    const [input, setInput] = useState('');
    const messagesEndRef = useRef(null);

    const onChange = (e) => {
        setInput(e.currentTarget.value);
    }

	const handleKeyPress = (e) => {
        if (e.key === "Enter") {
            e.preventDefault();
            if (isGeneratingAnswer) {
                return;
            }
            onClick();
        }
    };

	const handleButtonClick = (text) => {
		setInput(text);
	}

	const onClick = () => {
		if (isGeneratingAnswer) {
			return;
	  	}
	  	submitMessage(input);
		setInput('');
	}

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "auto" });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    return (
        <Container maxWidth="md" className={style.Chat__wrapper} sx={{ display: 'flex', flexDirection: 'column' }}>	

			{messages.length === 0 && (
				<>

				<Stack direction="column" sx={{ mb: '10vh', flex: '0 0 auto', flexWrap: 'wrap' }}>

					<Stack direction="row" sx={{ mb: '2vh', flex: '0 0 auto' }}>
						<Button
							sx={{ display: "flex", alignItems: "center", justifyContent: "center", height: "auto", flex: 1, top: '30vh',
								fontFamily: 'NanumSquare', bgcolor: '#F5F5F5', color: 'black', p: 2, borderRadius: 4, boxShadow: 'none', '&:hover': {backgroundColor: '#D8D8D8'} }} disableRipple>
							<TypographyComponent variant="h4" align="center" fontWeight="bold">ÏÇºÎã§Ïàò Ï∞æÏïÑÏ§ò</TypographyComponent>
						</Button>
					</Stack>
					<Stack direction="row" sx={{ mb: '2vh', flex: '0 0 auto' }}>
						<Button
							onClick={() =>
								handleButtonClick("Î¨¥ÎùºÎ≤® ÏÉùÏàò 2L 20Í∞ú ÏµúÏ†ÄÍ∞Ä Ï∞æÏïÑÏ§ò")
							}
							sx={{ display: "flex", alignItems: "center", justifyContent: "center", height: "auto", flex: 1, top: '30vh',
								fontFamily: 'NanumSquare', bgcolor: '#F5F5F5', color: 'black', p: 2, borderRadius: 4, boxShadow: 'none', '&:hover': {backgroundColor: '#D8D8D8'} }} disableRipple>
							<TypographyComponent variant="body1" align="left" fontWeight="medium">üí° "Î¨¥ÎùºÎ≤® ÏÉùÏàò 2L 20Í∞ú ÏµúÏ†ÄÍ∞Ä Ï∞æÏïÑÏ§ò" : Îã§ÏñëÌïú Ï°∞Í±¥ÏùÑ Ìè¨Ìï®ÌïòÏó¨ Íµ¨Ï≤¥Ï†ÅÏúºÎ°ú Ï∞æÏùÑ Ïàò ÏûàÏñ¥Ïöî.</TypographyComponent>
						</Button>
						<Button
							onClick={() =>
								handleButtonClick("Ïö©ÎèÑÏôÄ ÏÉÅÌô©Ïóê Îî∞Î•∏ ÏÉÅÌíà Ï∂îÏ≤úÏùÑ Î∞õÏùÑ Ïàò ÏûàÏñ¥Ïöî")
							}
							sx={{ display: "flex", alignItems: "center", justifyContent: "center", height: "auto", flex: 1, top: '30vh', marginLeft: '2vh', marginRight: '2vh',
								fontFamily: 'NanumSquare', bgcolor: '#F5F5F5', color: 'black', p: 2, borderRadius: 4, boxShadow: 'none', '&:hover': {backgroundColor: '#D8D8D8'} }} disableRipple>
							<TypographyComponent variant="body1" align="left" fontWeight="medium">üí° ‚ÄúÏßëÏóêÏÑú ÏÇ¨Ïö©Ìï† Î®∏Í∑∏Ïªµ Ï∂îÏ≤úÌï¥Ï§ò‚Äù : Ïö©ÎèÑÏôÄ ÏÉÅÌô©Ïóê Îî∞Î•∏ ÏÉÅÌíà Ï∂îÏ≤úÏùÑ Î∞õÏùÑ Ïàò ÏûàÏñ¥Ïöî.</TypographyComponent>
						</Button>
						<Button
							sx={{ display: "flex", alignItems: "center", justifyContent: "center", height: "auto", flex: 1, top: '30vh',
								fontFamily: 'NanumSquare', bgcolor: '#F5F5F5', color: 'black', p: 2, borderRadius: 4, boxShadow: 'none' }} disableRipple>
							<TypographyComponent variant="body1" align="left" fontWeight="medium">‚ö†Ô∏è Î∂ÄÏ†ïÌôïÌïú Ï†ïÎ≥¥Í∞Ä Ìè¨Ìï®Îê† Ïàò ÏûàÏñ¥Ïöî. ÏáºÌïëÎ™∞ ÎßÅÌÅ¨Ïùò ÏÉÅÌíà ÏÉÅÏÑ∏ ÌéòÏù¥ÏßÄÎ•º Íº≠ ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.</TypographyComponent>
						</Button>
					</Stack>
					<Stack direction="row" sx={{ mb: '2vh', flex: '0 0 auto' }}>
						<Button
							onClick={() =>
								handleButtonClick("Ï∂îÏ≤ú Î∞õÏùÄ ÏÉÅÌíàÎÅºÎ¶¨ Ïó¨Îü¨ ÏöîÏÜåÎ°ú ÎπÑÍµêÌï† Ïàò ÏûàÏñ¥Ïöî")
							}
							sx={{ display: "flex", alignItems: "center", justifyContent: "center", height: "auto", flex: 1, top: '30vh',
								fontFamily: 'NanumSquare', bgcolor: '#F5F5F5', color: 'black', p: 2, borderRadius: 4, boxShadow: 'none', '&:hover': {backgroundColor: '#D8D8D8'} }} disableRipple>
							<TypographyComponent variant="body1" align="left" fontWeight="medium">üí° "Îëê ÏÉÅÌíà Ï§ëÏóê Î¨¥ÏóáÏù¥ Îçî Ï†ÄÎ†¥Ìï¥?" : Ï∂îÏ≤ú Î∞õÏùÄ ÏÉÅÌíàÎÅºÎ¶¨ Ïó¨Îü¨ ÏöîÏÜåÎ°ú ÎπÑÍµêÌï† Ïàò ÏûàÏñ¥Ïöî.</TypographyComponent>
						</Button>
						<Button
							onClick={() =>
