import { useState, useEffect, useRef } from 'react';

import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import List from '@mui/material/List';
import Container from '@mui/material/Container';
import Stack from '@mui/material/Stack';
//import Grid from '@mui/material/Grid';

//import { IconButton } from '@mui/material';
//import SendIcon from '@mui/icons-material/Send';

import Message from '../Message';
import style from './Chat.module.scss';

const Chat = ({ messages, submitMessage, myId, isGeneratingAnswer }) => {
    const [input, setInput] = useState('');
    const messagesEndRef = useRef(null);

    const onChange = (e) => {
        setInput(e.currentTarget.value);
    }

	const handleKeyPress = (e) => {
        if (e.key === "Enter") {
            e.preventDefault();
            if (isGeneratingAnswer) {
                return;
            }
            onClick();
        }
    };

	const onClick = () => {
		if (isGeneratingAnswer) {
			return;
	  	}
	  	submitMessage(input);
		setInput('');
	}

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "auto" });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    return (
        <Container maxWidth="md" className={style.Chat__wrapper}>			
			<List className={style.Chat__list} >
				{messages.map(({userId, message}, index) => 
							  <Message 
								  key={`${userId}_${index}`}
								  userId={userId}
								  myId={myId}
								  text={message}
								  showName={!index || messages[index - 1].userId !== userId}
							  />
							 )}
			<div ref={messagesEndRef} />

			{messages.length === 0 && (
				<Stack direction="row" spacing={2} sx={{ mb: 1 }}>
					<Container maxWidth="sm" sx={{ bgcolor: 'info.main', color: 'info.contrastText', p: 2, borderRadius: 1, position: 'relative', top: '10px', flexGrow: 1, width: "25%"}}>
                        WOW 1
                    </Container>
                    <Container maxWidth="sm" sx={{ bgcolor: 'info.main', color: 'info.contrastText', p: 2, borderRadius: 1, position: 'relative', top: '10px', flexGrow: 1, width: "25%"}}>
                        WOW 2
                    </Container>
                    <Container maxWidth="sm" sx={{ bgcolor: 'info.main', color: 'info.contrastText', p: 2, borderRadius: 1, position: 'relative', top: '10px', flexGrow: 1, width: "25%"}}>
                        WOW 3
                    </Container>
                </Stack>
			)}
			</List>
			<Stack direction="row" className={style.Chat__input}>
				<TextField 
					className={style.Chat__input__textfield}
					onChange={onChange} 
					value={input} 
					placeholder="메시지를 입력하세요."
					onKeyPress={handleKeyPress}
					multiline
					minRows={1}
					maxRows={5}
				/>

				<Button 
					onClick={onClick}
					variant="outlined"
					disabled={isGeneratingAnswer}
					className={style.Chat__input__button}>전송</Button>

			</Stack>
        </Container>
    )
}

export default Chat;
