import { useEffect, useState, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { toast } from "react-toastify";
import axios from 'axios';

import ChatComp from '../components/Chat';
import { SocketContext } from '../contexts/SocketContext';

const Chat = () => {
    const { roomId, userId } = useParams();
    const { joinRoom, sendMessage, updateMessage } = useContext(SocketContext);
    const [ messages, setMessages ] = useState([]);
    const navigate = useNavigate();

    const addMessage = (message) => {
        setMessages((prev) => prev.concat(message));
    }

    useEffect(() => {
        try {
            joinRoom({userId, roomId});
            updateMessage(addMessage);
        } catch {
            navigate('/');
        }
    }, []);

	const getChatbotReply = async (userMessage) => {
		try {
		  const response = await axios.post('http://localhost:3001/chatbot', {
			message: userMessage,
		  });

		  if (response.data && response.data.reply) {
			return response.data.reply;
		  } else {
			throw new Error('Invalid server response');
		  }
		} catch (error) {
		  console.error(`Error while calling chatbot API: ${error.message}`);
		  return 'Error: Failed to get a reply from chatbot.';
		}
	  };

    const submitMessage = (message) => {
        if(message.trim()) {
            sendMessage({roomId, userId, message});
        } else {
            toast.error("메세지를 입력해주세요.")
        }

    }

    return (<ChatComp submitMessage={submitMessage} messages={messages} myId={userId}/>);
}

export default Chat;
